import React from 'react'
import { useState, useContext } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import SwatchPicker from './MySwatches/SwatchPicker'
import Swatch from './MySwatches/Swatch'
import MySwatch from './MySwatches/MySwatch'
import UserContext from '../Context/user-context'

const MySwatches = (props) => {
	const ctx = useContext(UserContext)
	const [isLoggedIn, setIsLoggedIn] = useState(true)
	const [swatchPickerOpen, setSwatchPickerOpen] = useState(true)

	//IDEA: make mySwatches obj into context api if logged in?

	const [mySwatches, setMySwatches] = useState([
		{
			name: 'swatch force one',
			id: 10,
			colors: [
				{
					id: 1,
					color: '#19b582',
					swatch: 'one',
				},
				{
					id: 2,
					color: '#1783bd',
					swatch: 'one',
				},
				{
					id: 3,
					color: '#fbde06',
					swatch: 'one',
				},
				{
					id: 4,
					color: '#8b9898',
					swatch: 'one',
				},
				{
					id: 5,
					color: '#355670',
					swatch: 'one',
				},
			],
		},
		{
			name: 'swatch force two',
			id: 20,
			colors: [
				{
					id: 1,
					color: '#19c782',
					swatch: 'two',
				},
				{
					id: 2,
					color: '#4783bd',
					swatch: 'two',
				},
				{
					id: 3,
					color: '#fbde41',
					swatch: 'two',
				},
				{
					id: 4,
					color: '#8b9898',
					swatch: 'two',
				},
				{
					id: 5,
					color: '#855670',
					swatch: 'two',
				},
			],
		},
	])

	const logInOut = () => {
		setIsLoggedIn(!isLoggedIn)
	}
	const togglePicker = () => {
		setSwatchPickerOpen(!swatchPickerOpen)
	}
	// console.log(ctx)
	const [colorsInSwatch, setColorsInSwatch] = useState([
		{
			name: 'base',
			id: 0,
			color: '#000000',
			colors: [
				{
					id: 1,
					color: '#000000',
				},
			],
		},
	])

	return (
		<UserContext.Consumer>
			{(ctx) => {
				return (
					<div className=''>
						<button onClick={logInOut}>LogIn/Out</button>
						<button onClick={togglePicker}>TogglePicker</button>
						{isLoggedIn && (
							<>
								{swatchPickerOpen && (
									<SwatchPicker
										setMySwatches={setMySwatches}
										mySwatches={mySwatches}
										colorsInSwatch={colorsInSwatch}
										setColorsInSwatch={setColorsInSwatch}
									/>
								)}
								<h1>THIS IS A LIST OF MYSWATCHES</h1>
								<div className='d-flex flex-wrap'>
									<div className='d-flex align-items-center justify-content-around '>
										{mySwatches.map((swatch, index) => (
											<MySwatch
												swatch={swatch}
												key={index}
												mySwatches={mySwatches}
												setMySwatches={setMySwatches}
											/>
										))}
									</div>
								</div>
								<h4>upgrade for more swatches</h4>
							</>
						)}
						{!isLoggedIn && (
							<div>
								<h1>Please log the fuck in</h1>
							</div>
						)}
					</div>
				)
			}}
		</UserContext.Consumer>
	)
}

export default MySwatches
