import styled from 'styled-components'

const ConverterStyle = styled.div`
	background-color: white;
	.header-converter {
		color: brown;
	}
	.input-converter {
		width: 100%;
		border: 1px solid lightgray;
	}
	.border-converter {
		border: 1px solid lightgray;
		border-radius: 1rem;
	}
	.border-bottom {
		border-bottom: 1px solid lightgray;
	}
`

export default ConverterStyle
