import React, { useEffect } from 'react'
import SwatchPickerStyle from './SwatchPickerStyle'
import chroma from 'chroma-js'
import { useState } from 'react'

const SwatchDiv = (props) => {
	const [textColor, setTextColor] = useState()
	console.log(chroma(props.bg.color).luminance())

	useEffect(() => {
		if (chroma(props.bg.color).luminance() < 0.3) {
			setTextColor('light')
		} else {
			setTextColor('dark')
		}
	}, [])

	return (
		<SwatchPickerStyle bg={props.bg.color}>
			<div className='swatch-div d-flex align-items-end'>
				<p className={textColor}>{props.bg.color}</p>
				<p className={textColor}>{textColor}</p>
			</div>
		</SwatchPickerStyle>
	)
}

export default SwatchDiv
