import React, { useLayoutEffect } from 'react'
import { HexColorPicker } from 'react-colorful'
import { RgbaStringColorPicker } from 'react-colorful'
import { HslaStringColorPicker } from 'react-colorful'
import { useState, useEffect } from 'react'
import SwatchDiv from './SwatchDiv'
import SwatchPickerStyle from './SwatchPickerStyle'
import chroma from 'chroma-js'
import MySwatches from '../MySwatches'

const SwatchPicker = ({ mySwatches, setMySwatches }) => {
	const [color, setColor] = useState('#aabbcc')
	const [colorFormat, setColorFormat] = useState('hex')
	const [name, setSwatchName] = useState('Larry')
	const [colorRemover, setColorRemover] = useState(false)

	const [colorsInSwatch, setColorsInSwatch] = useState([
		{
			name: 'base',
			id: 0,
			color: '#000000',
			colors: [
				{
					id: 1,
					color: '#000000',
				},
			],
		},
	])

	const hexFormat = () => {
		setColorFormat('hex')
		setColor(chroma(color).hex())
	}
	const rgbaFormat = () => {
		setColorFormat('rgba')
	}
	const hslaFormat = () => {
		setColorFormat('hsla')
	}

	const [newShit, setNewShit] = useState(colorsInSwatch[0].colors)
	const addColor = () => {
		const id = Math.floor(Math.random() * 10000) + 1
		const newColor = { id, color }
		setColorsInSwatch([...newShit, newColor])
		setNewShit([...newShit, newColor])
	}

	const saveButton = () => {
		const id = Math.floor(Math.random() * 100000) + 1
		const colors = colorsInSwatch
		const newSwatch = { id: id, colors, name }
		setMySwatches([...mySwatches, newSwatch])
	}

	const removeClickHandler = () => {
		setColorRemover(!colorRemover)
	}
	const deleteColor = (id) => {
		setColorsInSwatch(colorsInSwatch.filter((color) => color.id !== id))
		setNewShit(colorsInSwatch.filter((color) => color.id !== id))
	}

	return (
		<SwatchPickerStyle>
			<div className='container-create m-3'>
				<div className='col m-2'>
					<div className='row'>
						<div className='col-3'>
							<div className='button-container'>
								<button onClick={hexFormat}>hex</button>
								<button onClick={rgbaFormat}>rgba</button>
								<button onClick={hslaFormat}>hsla</button>
							</div>
							{colorFormat === 'hex' && (
								<>
									<div className='d-flex justify-content-center my-3'>
										<HexColorPicker color={color} onChange={setColor} />
									</div>
									<div className=''>
										<p>{color}</p>
									</div>
									<button onClick={addColor}>Add Color</button>
								</>
							)}
							{colorFormat === 'rgba' && (
								<>
									<div className='d-flex justify-content-center my-3'>
										<RgbaStringColorPicker color={color} onChange={setColor} />
									</div>
									<div>
										<p>{color}</p>
									</div>
									<button onClick={addColor}>Add Color</button>
								</>
							)}
							{colorFormat === 'hsla' && (
								<>
									<div className='d-flex justify-content-center my-3'>
										<HslaStringColorPicker color={color} onChange={setColor} />
									</div>
									<div>
										<p>{color}</p>
									</div>
									<button onClick={addColor}>Add Color</button>
								</>
							)}
						</div>
						<div className='col-6'>
							{colorsInSwatch && (
								<div className=' swatch-div-container d-flex align-items-center flex-wrap'>
									{colorsInSwatch.slice(1).map((color, index) => (
										<SwatchDiv
											bg={color}
											key={index}
											onDelete={deleteColor}
											colorRemover={colorRemover}
										/>
									))}
								</div>
							)}
						</div>
						<div className='col-3'>
							<p>{name} (required)</p>
							<input
								type='text'
								onChange={(e) => setSwatchName(e.target.value)}
							/>

							<button onClick={saveButton}>save Swatch</button>
							<div>
								<button onClick={removeClickHandler}>
									{colorRemover ? 'Done' : 'Remove Colors'}
								</button>
							</div>
						</div>
					</div>
				</div>
			</div>
		</SwatchPickerStyle>
	)
}

export default SwatchPicker
