import React from 'react'
import { HexColorPicker } from 'react-colorful'
import { RgbaStringColorPicker } from 'react-colorful'
import { HslaStringColorPicker } from 'react-colorful'
import { useState } from 'react'
import SwatchDiv from './SwatchDiv'
import SwatchPickerStyle from './SwatchPickerStyle'
import { Row } from 'reactstrap'

const SwatchPicker = () => {
	const [color, setColor] = useState('#aabbcc')
	const [rgbaColor, setRgbaColor] = useState('rgb(255, 255, 255)')
	const [hslaColor, setHslaColor] = useState('hsla(0, 0%, 100%, 1)')
	const [colorFormat, setColorFormat] = useState('hex')

	const [colorsInSwatch, setColorsInSwatch] = useState([
		{
			id: 1,
			color: '#19b582',
		},
		{
			id: 2,
			color: '#1783bd',
		},
		{
			id: 3,
			color: '#fbde06',
		},
		{
			id: 4,
			color: '#8b9898',
		},
		{
			id: 5,
			color: '#355670',
		},
	])

	const addColor = () => {
		const id = Math.floor(Math.random() * 10000) + 1
		const newColor = { id, color }
		console.log(newColor)
		setColorsInSwatch([...colorsInSwatch, newColor])
	}

	return (
		<SwatchPickerStyle>
			<div className='container-create'>
				<div className='col'>
					<div className='row'>
						<div className='col-3'>
							<div className='button-container'>
								<button onClick={() => setColorFormat('hex')}>hex</button>
								<button onClick={() => setColorFormat('rgba')}>rgba</button>
								<button onClick={() => setColorFormat('hsla')}>hsla</button>
							</div>
							{colorFormat === 'hex' && (
								<>
									<div className='d-flex justify-content-center my-3'>
										<HexColorPicker color={color} onChange={setColor} />
									</div>
									<div className=''>
										<p>{color}</p>
									</div>
									<button onClick={addColor}>Add Color</button>
								</>
							)}
							{colorFormat === 'rgba' && (
								<>
									<div className='d-flex justify-content-center my-3'>
										<RgbaStringColorPicker
											color={rgbaColor}
											onChange={(setRgbaColor, setColor)}
										/>
									</div>
									<div>
										<p>{rgbaColor}</p>
									</div>
									<button onClick={addColor}>Add Color</button>
								</>
							)}
							{colorFormat === 'hsla' && (
								<div>
									<HslaStringColorPicker
										color={hslaColor}
										onChange={setHslaColor}
									/>
									<p>{hslaColor}</p>
								</div>
							)}
						</div>

						<div className='col-6 swatch-div-container d-flex align-items-center'>
							{colorsInSwatch.map((color, index) => (
								<SwatchDiv bg={color} key={index} />
							))}
						</div>
						<div className='col-3'>
							<p>name</p>
							<button>save swatch</button>
						</div>
					</div>
				</div>
			</div>
		</SwatchPickerStyle>
	)
}

export default SwatchPicker
