import React from 'react'
import { HexColorPicker } from 'react-colorful'
import { RgbaColorPicker } from 'react-colorful'
import { HslaStringColorPicker } from 'react-colorful'
import { useState } from 'react'
import SwatchDiv from './SwatchDiv'

const SwatchPicker = () => {
	const [color, setColor] = useState('#aabbcc')
	const [rgbaColor, setRgbaColor] = useState({ r: 255, g: 255, b: 255, a: 1 })
	const [hslaColor, setHslaColor] = useState('hsla(0, 0%, 100%, 1)')
	const [colorFormat, setColorFormat] = useState('hsla')
	//Not rendering hsla or rgba, not sure why
	console.log({ hslaColor })
	return (
		<div>
			<div className='row'>
				<div className='col-4'>
					<button>change output format (hex/rgba/hsla)</button>
					{colorFormat === 'hex' && (
						<div>
							<HexColorPicker color={color} onChange={setColor} />
							<p>{color}</p>
						</div>
					)}
					{colorFormat === 'rgba' && (
						<div>
							<RgbaColorPicker color={rgbaColor} onChange={setRgbaColor} />
							{/* <p>{rgbaColor}</p> */}
						</div>
					)}
					{colorFormat === 'hsla' && (
						<div>
							<HslaStringColorPicker
								color={hslaColor}
								onChange={setHslaColor}
							/>
							<p>{hslaColor}</p>
						</div>
					)}
				</div>
				<div className='col-6'>
					<p>divs will go here(color squares)</p>
					<SwatchDiv />
				</div>
				<div className='col-2'>
					<p>name</p>
				</div>
			</div>
		</div>
	)
}

export default SwatchPicker
