import React from 'react'
import { useState } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import SwatchPicker from './MySwatches/SwatchPicker'
import Swatch from './MySwatches/Swatch'

const MySwatches = () => {
	const [isLoggedIn, setIsLoggedIn] = useState(true)
	const [swatchPickerOpen, setSwatchPickerOpen] = useState(true)
	//take in obj from SwatchPicker
	//query swatches database
	//map swatches over Swatch component
	const [mySwatches, setMySwatches] = useState([
		{
			name: 'swatch force one',
			id: 10,
			colors: [
				{
					id: 1,
					color: '#19b582',
					swatch: 'one',
				},
				{
					id: 2,
					color: '#1783bd',
					swatch: 'one',
				},
				{
					id: 3,
					color: '#fbde06',
					swatch: 'one',
				},
				{
					id: 4,
					color: '#8b9898',
					swatch: 'one',
				},
				{
					id: 5,
					color: '#355670',
					swatch: 'one',
				},
			],
		},
		{
			name: 'swatch force two',
			id: 20,
			colors: [
				{
					id: 1,
					color: '#19c782',
					swatch: 'two',
				},
				{
					id: 2,
					color: '#4783bd',
					swatch: 'two',
				},
				{
					id: 3,
					color: '#fbde41',
					swatch: 'two',
				},
				{
					id: 4,
					color: '#8b9898',
					swatch: 'two',
				},
				{
					id: 5,
					color: '#855670',
					swatch: 'two',
				},
			],
		},
	])

	const addSwatch = (addSwatch) => {
		const id = Math.floor(Math.random() * 10000) + 1
		const newSwatch = { id, ...addSwatch }
		setMySwatches([...mySwatches, newSwatch])
	}
	const logInOut = () => {
		setIsLoggedIn(!isLoggedIn)
	}

	return (
		<div className=''>
			<p>log in needed to view below this</p>
			<button onClick={logInOut}>Account/LogIn</button>
			{isLoggedIn && (
				<>
					{swatchPickerOpen && (
						<SwatchPicker
							setMySwatches={setMySwatches}
							mySwatches={mySwatches}
						/>
					)}
					<h1>THIS IS A LIST OF MYSWATCHES</h1>
					<div className='d-flex align-items-center justify-content-around'>
						{mySwatches.map((swatch, index) => (
							<Swatch swatch={swatch} key={index} addSwatch={{ addSwatch }} />
						))}
					</div>
					<h4>upgrade for more swatches</h4>
				</>
			)}
		</div>
	)
}

export default MySwatches
