import styled from 'styled-components'

const ConverterStyle = styled.div`
	.header-converter {
		color: white;
	}
	.input-converter {
		width: 100%;
		border: 1px solid lightgray;
	}
	.border-converter {
		border: 1px solid lightgray;
		border-radius: 1rem;
		box-shadow: 0px 7px 10px 2px #0000001a;
	}
	.border-bottom {
		border-bottom: 1px solid lightgray;
	}
`

export default ConverterStyle
