import React from 'react'
import SwatchDiv from './SwatchDiv'
import SwatchPickerStyle from './SwatchPickerStyle'

const Swatch = (props) => {
	//TODO: Use save button to add to MySwatches (if logged in)

	//IDEA: Make new component to display swatches in MySwatches (MySwatch.js)
	//Replace save with dynamic button with remove & edit
	const saveButton = () => {
		props.setMySwatches([...props.mySwatches, props.swatch])
		console.log(props.mySwatches)
	}

	return (
		<SwatchPickerStyle>
			<div className='swatch-container m-3'>
				<div className='d-flex justify-content-start'>
					{props.swatch.colors.map((color, index) => (
						<SwatchDiv bg={color} key={index} />
					))}
				</div>
				<div className='d-flex justify-content-between'>
					<p>{props.swatch.name}</p>
					<button className='m-1' onClick={saveButton}>
						Save
					</button>
				</div>
			</div>
		</SwatchPickerStyle>
	)
}

export default Swatch
