import { Routes, Route, Link } from 'react-router-dom'
import './App.css'
import CSS from './Components/CSS'
import MainPage from './Components/MainPage'
import MySwatches from './Components/MySwatches'
import UserContext from './Context/user-context'
import { useState } from 'react'

//IDEA: Add merch page later, stripe payments? 3rd QTR project
//IDEA: google ads, 3rd QTR
//IDEA: simple ads, links
//IDEA: CSS page

function App(props) {
	const [mySwatches, setMySwatches] = useState([
		{
			name: 'swatch force one',
			id: 10,
			colors: [
				{
					id: 1,
					color: '#19b582',
					swatch: 'one',
				},
				{
					id: 2,
					color: '#1783bd',
					swatch: 'one',
				},
				{
					id: 3,
					color: '#fbde06',
					swatch: 'one',
				},
				{
					id: 4,
					color: '#8b9898',
					swatch: 'one',
				},
				{
					id: 5,
					color: '#355670',
					swatch: 'one',
				},
			],
		},
		{
			name: 'swatch force two',
			id: 20,
			colors: [
				{
					id: 1,
					color: '#19c782',
					swatch: 'two',
				},
				{
					id: 2,
					color: '#4783bd',
					swatch: 'two',
				},
				{
					id: 3,
					color: '#fbde41',
					swatch: 'two',
				},
				{
					id: 4,
					color: '#8b9898',
					swatch: 'two',
				},
				{
					id: 5,
					color: '#855670',
					swatch: 'two',
				},
			],
		},
	])

	return (
		<UserContext.Provider
			value={{
				fuck: true,
			}}
		>
			<header className='App-header'>
				<div className='row fit'>
					<div className='col-8 m-3 mb-2'>
						<h1 className='header-main'>PoopSwatch.com</h1>
						<p>Color solutions for your shitty website</p>
					</div>
					<div className='col-3 d-flex align-items-end m-3 mb-2'>
						<nav className='p-3'>
							<Link to='/' className='p-2'>
								home
							</Link>
							<Link to='/MySwatches' className='p-2'>
								myswatches
							</Link>
							<Link to='/CSS' className='p-2'>
								CSS
							</Link>
							<Link to='/MySwatches' className='p-2'>
								Log In/Out
							</Link>
						</nav>
					</div>
				</div>
			</header>
			<main>
				<Routes>
					<Route
						path='/'
						element={
							<MainPage mySwatches={mySwatches} setMySwatches={setMySwatches} />
						}
					/>
					<Route path='css' element={<CSS />} />
					<Route
						path='MySwatches'
						element={
							<MySwatches
								mySwatches={mySwatches}
								setMySwatches={setMySwatches}
							/>
						}
					/>
					<Route path='share' element={<p>donate?</p>} />
				</Routes>
			</main>
			<footer className='footer text-center'>
				<p>some stupid ass footer</p>
			</footer>
		</UserContext.Provider>
	)
}

export default App
